<!DOCTYPE html><html><head><script src="http://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script><script>$(document).ready(function(){    $("#newSubject").hide();    $("#addSubject").hide();    $("#cancelButton").hide();    $("#displayField").click(function(){         $("#newSubject").toggle();         $("#addSubject").toggle();         $("#displayField").toggle();         $("#cancelButton").toggle();    });    $("#cancelButton").click(function(){         $("#newSubject").toggle();         $("#addSubject").toggle();         $("#displayField").toggle();         $("#cancelButton").toggle();    });	/*$("#addSubject").click(function(){        $.get("./listSubjectsJson", function(data, status){            alert("Data: " + data + "\nStatus: " + status);        });    });*/	$("#addSubject").click(function(){		//alert($("#newSubject").val());        $.post("./addSubject",        {          newSubject: $("#newSubject").val()        },        function(data,status){			//var names = data            //$('#body').html(data);			var json = $.parseJSON(data);			$(json).each(function(i,val){				$.each(val,function(k,v){				//alert(k);				$("#subject").val(v);//console.log(k+" : "+ v);     				});			});			$("#newSubject").toggle();			$("#addSubject").toggle();			$("#displayField").toggle();			$("#cancelButton").toggle();           // alert("Data: " + data + "\nStatus: " + status);		   /* $.each(data, function(index, element) {            $('body').append($('<div>', {                text: element.name            }));*/        });    });});</script></head><body><form action="addQuiz" method='post'><fieldset><legend>New Quiz</legend>Subject:<br><select name="subject" id="subject"><?php	foreach ($data as $row) {    echo "<option value='".$row['id']."'>".$row['description']."</option>";}?></select>&nbsp;&nbsp;<input type="button" id="displayField" value="New"/><input id="newSubject"/><input type="button" id="addSubject" value="Add"/>&nbsp;<input type="button" id="cancelButton" value="Cancel"/><br>Title:<br><input type="text" name="title" value=""><br><br><input type="submit" value="Submit"></fieldset></form></body></html>