<!DOCTYPE html><html><head><script src="http://ajax.googleapis.com/ajax/libs/jquery/1.11.2/jquery.min.js"></script><script>$(document).ready(function(){	$.get("./listSubjects",        function(data, status){			var primeiro = true;			var selected = "selected";        $.each(data, function(i, item) {				if(!primeiro)					selected = "";				$("#subject").append("<option value='"+item.id+"' " + selected + ">" + item.description + "</option>");				primeiro = false;			});		},		'json'		);    $("#newSubject").hide();    $("#addSubject").hide();    $("#cancelButton").hide();	    $("#displayField").click(function(){         $("#newSubject").toggle();         $("#addSubject").toggle();         $("#displayField").toggle();         $("#cancelButton").toggle();    });	    $("#cancelButton").click(function(){         $("#newSubject").toggle();         $("#addSubject").toggle();         $("#displayField").toggle();         $("#cancelButton").toggle();    });		$("#addSubject").click(function(){    $.post("./addSubject",    {         newSubject: $("#newSubject").val()    },    function(data, status){		var selected = "";		$("#subject").html("");       $.each(data, function(i, item) {				if($("#newSubject").val() == item.description)					selected = "selected";				else					selected = "";				$("#subject").append("<option value='"+item.id+"' " + selected + ">" + item.description + "</option>");			});		$("#newSubject").val("");		$("#newSubject").toggle();       $("#addSubject").toggle();       $("#displayField").toggle();       $("#cancelButton").toggle();    },	'json'		);});});</script></head><body><form action="addQuiz" method='post'><fieldset><legend>New Quiz</legend>Subject:<br><select name="subject" id="subject"></select>&nbsp;&nbsp;<input type="button" id="displayField" value="New"/><input id="newSubject"/><input type="button" id="addSubject" value="Add"/>&nbsp;<input type="button" id="cancelButton" value="Cancel"/><br>Title:<br><input type="text" name="title" value=""><br><br><input type="submit" value="Submit"></fieldset></form></body></html>